import React, { useState } from 'react';
import './Expenses.css';
import ExpenseItem from './ExpenseItem';
import ExpensesFilter from './ExpensesFilter';
import Card from '../UI/Card';

const Expenses = (props) => {

  const expenses = props.data;

  const [filteredYear, setFilteredYear] = useState('2020');

  // 필터링해서 필터링된 리스트를 맵핑할 수 있게
  const filterExpensesHandler = year => {
    return expenses.filter(expense => expense.date.getFullYear().toString() === year.toString());
  }

  const [filteredExpenses, setFilteredExpenses] = useState(filterExpensesHandler(filteredYear));


  const filterChangeHandler = selectedYear => {
    setFilteredYear(selectedYear);
    setFilteredExpenses(filterExpensesHandler(selectedYear));
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter selected={filteredYear} onChangeFilter={filterChangeHandler} />
        {filteredExpenses.map((x, i) => {
          return (
            <ExpenseItem
              key={x.id}
              title={x.title}
              amount={x.amount}
              date={x.date}
            />
          )
        })}
      </Card>
    </div>
  );
};

export default Expenses;
